def on_a_pressed():
    global start_text, in_menu, menu_tienda_abierta, menu_shop
    menu_items_activo = 0
    if start_text:
        start_text = True
        in_menu = False
        menu()
    if shop.overlaps_with(nena) and (not (menu_tienda_abierta) and not (menu_items_activo)):
        menu_tienda_abierta = True
        sprites.destroy(nena)
        menu_shop = miniMenu.create_menu(miniMenu.create_menu_item("Gallina = 6 de leña", assets.image("""
                gallina
                """)),
            miniMenu.create_menu_item("Patatas (1,5kg) = 2 de leña",
                assets.image("""
                    patata
                    """)),
            miniMenu.create_menu_item("Cabra = 5 de leña", assets.image("""
                cabra
                """)),
            miniMenu.create_menu_item("Huevo (12) = 3 de leña",
                assets.image("""
                    huevo
                    """)),
            miniMenu.create_menu_item("Caballo = 12 de leña",
                assets.image("""
                    caballo
                    """)),
            miniMenu.create_menu_item("Salir"))
        menu_shop.set_position(80, 60)
        
        def on_button_pressed2(selection2, selectedIndex2):
            global cantidad_gallina, cantidad_patatas, cantidad_cabra, cantidad_huevos, cantidad_caballos
            menu_shop.close()
            if selectedIndex2 == 0:
                cantidad_gallina += 1
            elif selectedIndex2 == 1:
                cantidad_patatas += 1
            elif selectedIndex2 == 2:
                cantidad_cabra += 1
            elif selectedIndex2 == 3:
                cantidad_huevos += 1
            elif selectedIndex2 == 4:
                cantidad_caballos += 1
        menu_shop.on_button_pressed(controller.A, on_button_pressed2)
        
controller.A.on_event(ControllerButtonEvent.PRESSED, on_a_pressed)

